package wukai1;

public class Exe03 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =1;
		int b =2;
		System.out.println("a+b="+(a+b)+"\n"+"a-b="+(a-b)+"\n"+"a*b="+(a*b)+"\n"+"a/b="+(a/b));
		float c=1.0f;
		float d=2.0f;
		System.out.println("c+d="+(c+d)+"\n"+"c-d="+(c-d)+"\n"+"c*d="+(c*d)+"\n"+"c/d="+(c/d));
		double e=2.0;
		double f=3.0;
		System.out.println("e+f="+(e+f)+"\n"+"e-f="+(e-f)+"\n"+"e*f="+(e*f)+"\n"+"e/f="+(e/f));

	}

}
